  // let e = document.getElementById("project");

  // e.onmouseover = function () {
  //   setHoverStatus(true);
  // };

  // e.onmouseout = function () {
  //   setHoverStatus(false);
  // };

  return (
    <div class="col-sm">
      <div
        class="card text-center project"
        onMouseEnter={() => setHoverStatus(true)}
        onMouseLeave={() => setHoverStatus(false)}
      >
        <h2>MyTinerary</h2>
        {hoverStatus ? (
          <p>
            Web-based trip planning tool for groups of travelers to streamline
            expenses
          </p>
        ) : null}

        {/* <div class="card-body">
                <h5 class="card-title">MyTinerary</h5>

                <p class="card-text">
                  Web-based trip planning tool for groups of travelers to
                  streamline expenses
                </p>
              </div> */}
      </div>
    </div>
  );
};

export default Project;
